    private void ButtonMostrarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ButtonMostrarActionPerformed

        int horas=Integer.parseInt(FieldHoras.getText());
        double tarifa=Double.parseDouble(FieldTarifas.getText());

        double basico=horas*tarifa;
        double bonificacion=basico*0.2;
        double neto=(basico+bonificacion)*0.9;

        TextArea.setText(" ** Resumen de Pago a Empleadp ** ");
        TextArea.append("El basico es: "+basico);
        TextArea.append(" La bonifiaci√≥n es: "+bonificacion);
        TextArea.append("El neto es: "+neto);
        // TODO add your handling code here:
    }//GEN-LAST:event_ButtonMostrarActionPerformed

